import { useCart } from "../store/CartContext";

function ShoppingCart() {
  const { cart, removeFromCart, increaseQuantity, decreaseQuantity } = useCart();

  const totalPrice = cart.reduce((total, item) => total + item.price * item.quantity, 0);

  if (cart.length === 0) {
    return (
      <div>
        <p>Your cart is empty.</p>
        <p>Go to Products</p>
      </div>
    );
  }

  return (
    <div>
      <h1>Shopping Cart</h1>
      <h2>Total Price: ${totalPrice.toFixed(2)}</h2> {/* Display total price */}
      {cart.map((item) => (
        <div
          key={item.id}
          style={{
            display: 'flex',
            alignItems: 'center',
            borderBottom: '1px solid #ccc',
            marginBottom: '20px',
            padding: '10px',
          }}
        >
          <img
            src={item.image}
            alt={item.title}
            style={{ width: '100px', height: '100px', objectFit: 'contain', marginRight: '20px' }}
          />
          <div style={{ flex: 1 }}>
            <h2>{item.title}</h2>
            <p>Price: ${item.price.toFixed(2)}</p>
            <p>Quantity: {item.quantity}</p>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <button onClick={() => decreaseQuantity(item.id)} style={{ padding: '5px 10px' }}>
              -
            </button>
            <button onClick={() => increaseQuantity(item.id)} style={{ padding: '5px 10px' }}>
              +
            </button>
            <button onClick={() => removeFromCart(item.id)} style={{ padding: '5px 10px', cursor: 'pointer' }}>
              Remove
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ShoppingCart;
