import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import "../style.css";

const Navbar: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);

  const navButton = "text-md py-2 px-4 rounded-md hover:bg-green1 transition-colors duration-300";
  const navButtons= "w-full text-md py-2 px-20 rounded-md hover:bg-green1 transition-colors duration-300";
  return (
    <header className="bg-orange1 sticky top-0 w-full shadow-md z-50">
  <nav className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
    <div className="flex justify-between items-center py-4">
      {/* Logo or Title */}
      <h1 className="text-white1 text-2xl font-bold">Online Shop</h1>

      {/* Hamburger Menu for Small Screens */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="md:hidden focus:outline-none text-white1 text-lg font-semibold"
      >
        {isOpen ? 'Close' : 'Menu'}
      </button>

      {/* Desktop Menu */}
      <ul className="hidden md:flex space-x-8 text-lg font-medium">
        <li><Link to="/" className={navButton}>Home</Link></li>
        <li><Link to="/products" className={navButton}>Our Products</Link></li>
        <li><Link to="/cart" className={navButton}>Shopping Cart</Link></li>
        <li><Link to="/userDetail" className={navButton}>User Detail</Link></li>
      </ul>
    </div>

    {/* Mobile Menu */}
    {isOpen && (
      <div className="md:hidden bg-orange1 rounded-lg">
        <ul className="flex flex-col space-y-4 text-lg font-medium p-4 text-center text-white1">
          <li><Link to="/" className={navButtons}>Home</Link></li>
          <li><Link to="/products" className="">Our Products</Link></li>
          <li><Link to="/cart" className="">Shopping Cart</Link></li>
          <li><Link to="/userDetail" className="">User Detail</Link></li>
        </ul>
      </div>
    )}
  </nav>
</header>
  );}

export default Navbar;
