import { memo } from "react";
import { Product } from "../Types/Type";

const ProductCard = memo(({ product, onAddToCart }:
    { product: Product; onAddToCart: (product: Product) => void }) => (
        <div className="bg-warm relative hover:text-lightGrey hover:bg-warm hover:scale-105 transition-all duration-200 rounded-lg p-4 shadow-lg flex flex-col h-full">
            <img src={product.image} alt={product.title} className="w-full h-48 object-contain mb-4 rounded-lg" />
            <h2 className="self-center text-lg font-semibold text-gray-800 mb-2 line-clamp-2">{product.title}</h2>
            <div className="flex flex-col space-y-4 flex-grow justify-between">
                <p className="text-lg font-semibold text-right text-gray-800">{`$${product.price.toFixed(2)}`}</p>
                <button
                   onClick={() => onAddToCart(product)}
                   className="hover:text-darkblue bg-orange1 hove:bg-green1 text-white py-2 px-4 rounded-lg w-7/10 sm:w-full transition hover:bg-orange1 hover:scale-105">
                      Add to Cart
                </button>
            </div>
        </div>
));

export default ProductCard;
