import { memo } from "react";
import { Product } from "../Types/Type";

const ProductCard = memo(({ product, onAddToCart }:
    { product: Product; onAddToCart: (product: Product) => void }) => (
        <div className="relative bg-lightPurple rounded-lg overflow-hidden transition-transform duration-300 hover:scale-105 hover:bg-purple-700 shadow-lg">
      <img
        src={product.image}
        alt={product.title}
        className="w-full h-48 object-contain mb-4"
      />
      <div className="p-4">
        <h2 className="text-lg font-semibold text-gray-800 mb-2">{product.title}</h2>
        <div className="absolute bottom-16 left-4 right-4 flex justify-between items-center">
          <p className="text-gray-600 text-lg font-semibold">{`$${product.price.toFixed(2)}`}</p>
        </div>
        <button
          onClick={() => onAddToCart(product)}
          className="w-7/12 sm:w-full mx-auto py-2 px-4 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition duration-300"
        >
          Add to Cart
        </button>
      </div>
    </div>
  
  ));

  export default ProductCard;
