import { memo } from "react";
import { Product } from "../Types/Type";

const ProductCard = memo(({ product, onAddToCart }:
    { product: Product; onAddToCart: (product: Product) => void }) => (
        <div className="bg-lightPurple rounded-lg shadow-md hover:scale-105 hover:bg-purple-500 transition-all duration-200 p-4 relative">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-48 object-contain mb-4 rounded-t-lg"
        />
        <h2 className="text-lg font-semibold text-gray-800 mb-2">{product.title}</h2>
        <p className="text-gray-600 mb-2">Price: ${product.price.toFixed(2)}</p>
        
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 w-7/10 sm:w-full">
          <button
            onClick={() => onAddToCart(product)}
            className="bg-purple-600 text-white py-2 px-4 rounded-lg hover:bg-purple-700 transition duration-200 w-full"
          >
            Add to Cart
          </button>
        </div>
      </div>
  ));

  export default ProductCard;
