import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useCart } from '../Store/CartContext';
import { Product } from '../Types/Type';
import SearchAndFilter from './Search&Filter';

const Products = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const { addToCart } = useCart();

  useEffect(() => {
    fetch('https://fakestoreapi.com/products')
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error(err));
  }, []);
  
  const filteredProducts = useMemo<Product[]>(() => {
    return products.filter((product) => {
      return (
        (!searchQuery || product.title.toLowerCase().includes(searchQuery.toLowerCase())) &&
        (!categoryFilter || product.category === categoryFilter)
      );
    });
  }, [products, searchQuery, categoryFilter]);

  const handleAddToCart = useCallback((product: Product) => {
    addToCart(product);
  }, [addToCart]);

  return (
    <div>
      <h1>Products</h1>
      <SearchAndFilter
        searchQuery={searchQuery}
        setSearchQuery={setSearchQuery}
        categoryFilter={categoryFilter}
        setCategoryFilter={setCategoryFilter}
      />
      <div className="grid grid-cols-auto-fill gap-5">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <ProductCard
              key={product.id}
              product={product}
              onAddToCart={handleAddToCart}
            />
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </div>
    </div>
  );
};

// Memoized product card to avoid re-rendering unless props change
const ProductCard = React.memo(({ product, onAddToCart }: { product: Product; onAddToCart: (product: Product) => void }) => (
  <div className="border p-4 rounded-md shadow">
    <img
      src={product.image}
      alt={product.title}
      className="w-full h-48 object-contain mb-2"
    />
    <h2 className="text-lg font-bold">{product.title}</h2>
    <p>Price: ${product.price.toFixed(2)}</p>
    <button
      onClick={() => onAddToCart(product)}
      className="bg-purple-600 text-white py-1 px-3 rounded mt-2 hover:bg-purple-700"
    >
      Add to Cart
    </button>
  </div>
));

export default Products;
