import { Link } from "react-router-dom";
import { useCart } from "../Store/CartContext";
import "../style.css";
import { useMemo } from "react";

function ShoppingCart() {
  const { cart, removeFromCart, increaseQuantity, decreaseQuantity } = useCart();

  const totalPrice = useMemo(() => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  }, [cart]);
  
  if (cart.length === 0) {
    return (
    <div>
      <h1>Your Shopping Cart</h1>
      <p>Your cart is empty.</p>
      <Link to={"/products"}>Go to Products</Link>
    </div>
    );
  }

  const tdStyle = { padding: '10px', border: '1px solid #ccc' };  

  return (
    <div style={{ padding: '20px' }}>
      <h1>Your Shopping Cart</h1>
      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0', textAlign: 'center' }}>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Number</th>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Title</th>
            <th className="description-header" style={{ padding: '10px', border: '1px solid #ccc' }}>Description</th>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Quantity</th>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Price</th>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Total Price</th>
            <th style={{ padding: '10px', border: '1px solid #ccc' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item, index) => (
            <tr key={item.id} style={{ textAlign: 'center' }}>
              <td style={tdStyle}>{index + 1}</td>
              <td style={tdStyle}>{item.title}</td>
              <td className="description-cell" style={tdStyle}>{item.description || 'N/A'}</td>
              <td style={tdStyle}>
                <button onClick={() => decreaseQuantity(item.id)} style={{ margin: '0 5px', backgroundColor: 'red', color: 'white', padding: '5px 6px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>-</button>
                {item.quantity}
                <button onClick={() => increaseQuantity(item.id)} style={{ margin: '0 5px', backgroundColor: 'green', color: 'white',padding: '5px 5px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>+</button>
              </td>
              <td style={tdStyle}>${item.price.toFixed(2)}</td>
              <td style={tdStyle}>${(item.price * item.quantity).toFixed(2)}</td>
              <td style={tdStyle}>
                <button onClick={() => removeFromCart(item.id)} style={{ backgroundColor: 'red', color: 'white', padding: '5px 10px', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
                  Remove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          <tr style={{ textAlign: 'center', fontWeight: 'bold' }}>
            <td colSpan={5} style={{ padding: '10px', border: '1px solid #ccc' }}>Total</td>
            <td style={{ padding: '10px', border: '1px solid #ccc' }}>${totalPrice.toFixed(2)}</td>
            <td style={{ padding: '10px', border: '1px solid #ccc' }}></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
}

export default ShoppingCart;
