import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import "../style.css";

const Navbar: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);

  const navButton = "py-2 px-2 mx-0 rounded-md hover:text-darkblue text-white hover:bg-red1 transition-colors duration-300";

  return (
    <header className="bg-darkblue sticky top-0 bg-purple-600 text-white shadow-md z-50">
      <nav className="text-white max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center py-4">
          <h1 className="text-white text-2xl font-bold">Shop Name</h1>

          {/* Toggle button for mobile */}
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="md:hidden focus:outline-none text-white hover:text-purple-300 transition-colors duration-300"
          >
            {isOpen ? 'Close' : 'Menu'}
          </button>

          {/* Links for larger screens */}
          <ul className="hidden md:flex space-x-8 text-lg font-medium">
            <li>
              <Link to="/" className={navButton}>Home</Link>
            </li>
            <li>
              <Link to="/products" className={navButton}>Our Products</Link>
            </li>
            <li>
              <Link to="/cart" className={navButton}>Shopping Cart</Link>
            </li>
            <li>
              <Link to="/userDetail" className={navButton}>User Detail</Link>
            </li>
          </ul>
        </div>
        {isOpen && (
          <div className="md:hidden flex items-center justify-center min-h-[60vh]">
            <ul className="flex flex-col space-y-4 text-lg font-medium bg-purple-500 p-6 rounded-lg max-w-md text-center">
              <li><Link to="/" className="w-[100%] py-2 px-2 mx-0 rounded-md hover:text-darkblue text-white hover:bg-red1 transition-colors duration-300">Home</Link></li>
              <li><Link to="/products" className="hover:text-purple-300">Our Products</Link></li>
              <li><Link to="/cart" className="hover:text-purple-300">Shopping Cart</Link></li>
              <li><Link to="/userDetail" className="hover:text-purple-300">User Detail</Link></li>
            </ul>
          </div>
        )}
      </nav>
    </header>
  );
};

export default Navbar;
