import { Link } from "react-router-dom";
import cartImage from "../PIC/home.jpg";

export default function HomePage() {
  const catBox1 = "text-lg bg-warm2 text-white p-4 rounded-lg text-center font-semibold";
  const catBox2 = "text-lg bg-warm text-white p-4 rounded-lg text-center font-semibold";

  return (
    <div className="min-h-screen flex flex-col md:flex-row bg-Lblue1 overflow-hidden md:overflow-auto">
      {/* Left Section */}
      <div className="flex flex-col justify-center items-center md:w-2/3 p-8">
        <h1 className="text-4xl font-bold mb-4 text-center">Welcome to Our Online Shop!</h1>
        <p className="text-lg mb-6 text-center">
          Lorem ipsum dolor sit amet consectetur, adipisicing elit.
        </p>

        <div className="grid grid-cols-2 gap-4 mb-6 w-full max-w-md">
          <div className={catBox1}>Electronics</div>
          <div className={catBox1}>Jewelry</div>
          <div className={catBox2}>Men's Clothing</div>
          <div className={catBox2}>Women's Clothing</div>
        </div>

        <Link
          to="/products"
          className="bg-orange1 hover:bg-orange2 text-white px-8 py-4 rounded-lg text-lg font-semibold w-3/4 md:w-full text-center"
        >
          Our Products
        </Link>
      </div>

      {/* Right Section with Blur Effect */}
      <div className="md:w-4/10 h-80 md:h-auto flex items-center justify-center">
        <div className="w-full h-full relative">
          <img
            src={cartImage}
            alt="A cart"
            className="w-full h-full object-cover rounded-lg filter blur-md"
          />
          <div className="absolute inset-0 bg-black bg-opacity-30 rounded-lg"></div>
        </div>
      </div>
    </div>
  );
}
