import { Link } from "react-router-dom";
import { useCart } from "../Store/CartContext";
import "../style.css";
import { useMemo } from "react";

function ShoppingCart() {
  const { cart, removeFromCart, increaseQuantity, decreaseQuantity } = useCart();

  const totalPrice = useMemo(() => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  }, [cart]);
  
  if (cart.length === 0) {
    return (
    <div>
      <h1>Your Shopping Cart</h1>
      <p>Your cart is empty.</p>
      <Link to={"/products"}>Go to Products</Link>
    </div>
    );
  }

  const tdStyle = { padding: '10px', border: '1px solid #ccc' };  

  return (
    <div className="p-8">
  <h1 className="text-3xl font-bold mb-6 text-center">Your Shopping Cart</h1>
  <table className="w-full border-collapse">
    <thead>
      <tr className="bg-gray-200 text-center">
        <th className="p-4 border border-gray-300">Number</th>
        <th className="p-4 border border-gray-300">Title</th>
        <th className="p-4 border border-gray-300">Description</th>
        <th className="p-4 border border-gray-300">Quantity</th>
        <th className="p-4 border border-gray-300">Price</th>
        <th className="p-4 border border-gray-300">Total Price</th>
        <th className="p-4 border border-gray-300">Actions</th>
      </tr>
    </thead>
    <tbody>
      {cart.map((item, index) => (
        <tr key={item.id} className="text-center hover:bg-gray-100">
          <td className="p-4 border border-gray-300">{index + 1}</td>
          <td className="p-4 border border-gray-300">{item.title}</td>
          <td className="p-4 border border-gray-300">{item.description || 'N/A'}</td>
          <td className="p-4 border border-gray-300">
            <div className="flex justify-center items-center space-x-2">
              <button 
                onClick={() => decreaseQuantity(item.id)} 
                className="px-3 py-1 bg-red-500 text-white rounded-md hover:bg-red-600"
              >
                -
              </button>
              <span>{item.quantity}</span>
              <button 
                onClick={() => increaseQuantity(item.id)} 
                className="px-3 py-1 bg-green-500 text-white rounded-md hover:bg-green-600"
              >
                +
              </button>
            </div>
          </td>
          <td className="p-4 border border-gray-300">${item.price.toFixed(2)}</td>
          <td className="p-4 border border-gray-300">${(item.price * item.quantity).toFixed(2)}</td>
          <td className="p-4 border border-gray-300">
            <button 
              onClick={() => removeFromCart(item.id)} 
              className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
            >
              Remove
            </button>
          </td>
        </tr>
      ))}
    </tbody>
    <tfoot>
      <tr className="font-bold text-center">
        <td colSpan={5} className="p-4 border border-gray-300">Total</td>
        <td className="p-4 border border-gray-300">${totalPrice.toFixed(2)}</td>
        <td className="p-4 border border-gray-300"></td>
      </tr>
    </tfoot>
  </table>
</div>

  );
}

export default ShoppingCart;
